import logo from './logo.svg';
import './App.css';
import React, {useEffect, useState} from 'react';

function App() {
  const [firstNameInputValue, setFirstName] = useState("");
  const [lastNameInputValue, setLastName] = useState("");
  const [errorMessage, setErrorMessage] = useState([]);

  const handleSubmit = (firstName, lastName) => {
    const allowAlphabetsRegex = new RegExp(/^[A-Za-z]+$/);
    const errors = [];

    if (!allowAlphabetsRegex.test(firstName)){
      errors.push("Invalid First Name");
    }

    if (!allowAlphabetsRegex.test(lastName)){
      errors.push("Invalid Last Name");
    }

    if (errors.length === 0){

    }else {
      setErrorMessage(errors);
    }
  }

  return (
    <div className="App">
      <input type="text" 
        placeholder="First Name" 
        style={{marginTop: "24px"}}
        onChange={(event) => setFirstName(event.target.value)}/>

      <input type="text" 
        placeholder="Last Name" 
        style={{marginTop: "24px"}}
        onChange={(event) => setLastName(event.target.value)}/>

      <button onClick={() => handleSubmit(firstNameInputValue, lastNameInputValue)}>Submit</button>
      <div>
        {
          errorMessage.map(errorMessage => (
            <p>{errorMessage}</p>
          ))
        }
      </div>
    </div>
  );
}

export default App;
